require_once('workflows.php');

$w = new Workflows();
$query = urlencode( "{query}" );
$username = $w->get( 'github.username', 'settings.plist' );

$url = "https://api.github.com/users/".$username."/gists";
$content = $w->request( $url );
$gists = json_decode( $content );

foreach($gists as $gist ):
	$name_array = object_to_array($gist->files);
	$name = array_keys($name_array);

if (strlen(strstr( strtolower($name[0]), strtolower($query)))>0) {
 	$w->result( $gist->html_url, $gist->html_url, $name[0], $gist->description, 'gist_item.png' );
}

endforeach;

if ( count( $w->results() ) == 0 ):
  $w->result( 'git', 'na', 'No Gist found', 'No Gist were found that match your query', 'gist_item.png', 'no' );
endif;

echo $w->toxml();

function object_to_array($data)
{
    if (is_array($data) || is_object($data))
    {
        $result = array();
        foreach ($data as $key => $value)
        {
            $result[$key] = object_to_array($value);
        }
        return $result;
    }
    return $data;
}

